import css from "./Header.module.css"
import {NavLink} from "react-router-dom";
import {useState} from "react";

const Header = (props) => {
    let [miniInfo, setMiniInfo] = useState();
    return (
        <div className={css.header}>

            <div>
                <NavLink style={css} to="/profile/">Profile</NavLink>
            </div>
            <div>
                <NavLink to="/messages">Messages</NavLink>
            </div>
            <div>
                <NavLink to="/users">Users</NavLink>
            </div>

            <div className={css.loginBlock}>
                {props.loggedIn
                    ? <div className={css.login}>
                        <span>{props.login}</span>
                        <button onClick={props.logoutThunkCreator}><img width={"20px"} src={"https://cdn-icons.flaticon.com/png/512/3889/premium/3889524.png?token=exp=1648247613~hmac=18c503f32282bbe72ec582a1890f6a42"}/></button>
                    </div>
                    : <NavLink to={"/login"}>Login</NavLink>}
            </div>
        </div>
    );
}

export default Header;
