import UsersAPI from "./UsersAPI";
import {connect} from "react-redux";
import {followAC, getUsersThunkCreator} from "../../../Redux/users-reducer";

let mapStateToProps = (state) => {
    return {
        users: state.usersData.users,
        pageInfo: state.usersData.pageInfo,
        followingInProgress:state.usersData.pageInfo.isFollowing
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        follow: (userId) => {
           dispatch(followAC(userId));
        },
        unfollow(userId){
            dispatch({type:"UNFOLLOW", userId:userId});
        },
        setUsers(users){
            dispatch({type:"SET-USERS", users:users});
        },
        setCurrentPage(currentPage){
            dispatch({type:"SET-CURRENT-PAGE", currentPage:currentPage});
        },
        isLoading(isLoading){
            dispatch({type:"IS-LOADING", isLoading:isLoading})
        },
        isFollowing(isFollowing, userId){
            dispatch({type:"IS-FOLLOWING", isFollowing:isFollowing, userId:userId})
        },
        getUsersThunkCreator(currentPage, pageSize){
            getUsersThunkCreator(currentPage, pageSize);
        }
    }
}

let UserContainer = connect(mapStateToProps, mapDispatchToProps)(UsersAPI);
export default UserContainer
