import './App.css';
import Navbar from './components/Navbar/Navbar.js';
import Content from './components/Content/Content';
import {BrowserRouter} from 'react-router-dom';
import HeaderContainer from "./components/Header/HeaderContainer";
import {Component} from "react";
import {connect} from "react-redux";
import {initThunkCreator} from "./Redux/app-reducer";
import Preloader from "./components/common/preloader/preloader";

class App extends Component {

    componentDidMount() {
        this.props.initThunkCreator();
        console.log("did mount app");
        window.i = 1;
    }

    render() {
        console.log("render app")
        if (!this.props.initialized){
            return <Preloader/>
        }
        else return (
            <div className="app-wrapper">
                <BrowserRouter>
                    <HeaderContainer/>
                    <Navbar/>
                    <Content/>
                </BrowserRouter>
                <button onClick={()=>{window.i++; this.forceUpdate()}}> {window.i}</button>
            </div>
        );
    }
}

const mapStateToProps=(state)=>({
    initialized: state.app.initialized,
})

export default connect(mapStateToProps, {initThunkCreator})(App);
