export let store = {
    state: {
        messageData: {
            dialogs: [
                {
                    name: "stepa",
                    messages: [
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brahh"},
                        {name: "user1", message: "sup brahhhh"},
                        {name: "user1", message: "sup brahhhhhhhhh"}]
                },
                {
                    name: "pasha",
                    messages: [
                        {name: "user1", message: "supes brahes"},
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brah"}
                    ]
                },
                {
                    name: "sanya",
                    messages: [
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brah"}]
                }
            ],
            newMessageText: {name: "user1", message: "shieeeet"},
        },
        postData: {
            posts: [
                {id: "1", likesCount: 3, text: "jopa"},
                {id: "2", likesCount: 6, text: "jopaa"},
                {id: "3", likesCount: 2, text: "jopaaa"},
                {id: "4", likesCount: 1, text: "jopaaaaaa"}
            ],
            newPostText: {id: "5", likesCount: 0, text: "asdasda"}
        }
    },

    dispatch(action) {
        if (action.type == "ADD-POST") {
            this._addPost();
        } else if (action.type == "CHANGE-POST") {
            this._postTextChange(action.text);
        } else if (action.type == "ADD-MESSAGE") {
            this._addMessage();
        } else if (action.type == "CHANGE-MESSAGE") {
            this._messageTextChange(action.text);
        } else console.log("unexpected action value, current value: " + action.type);
    },
    rerenderEntireTree() {
    },
    subscriber(observer) {
        this.rerenderEntireTree = observer;
    },
    _addPost() {
        this.state.postData.posts.push(Object.assign({}, this.state.postData.newPostText));
        //postTextChange("");
        this.rerenderEntireTree();
    },
    _postTextChange(newText) {
        this.state.postData.newPostText.text = newText;
        this.rerenderEntireTree();
    },
    _addMessage() {
        debugger;
        this.state.messageData.dialogs[0].messages.push(this.state.messageData.newMessageText);
        this.rerenderEntireTree();
    },
    _messageTextChange(newText) {
        this.state.messageData.newMessageText.message = newText;
        this.rerenderEntireTree();
    },
    getState() {
        return this.state;
    }

}
window.store = store;
export default store;
