import {usersAPI} from "../ api/api";

let initValue = {
        users: [],
        pageInfo: {
            pageCount: 4,
            pageSize: 2,
            currentPage: 1,
            isLoading: true,
            isFollowing: [],
        }
    }
;
const usersReducer = (state = initValue, action) => {
    switch (action.type) {
        case "FOLLOW": {
            console.log("followed")
            let stateCopy = {...state};
            stateCopy.users = [...state.users];
            stateCopy.users.map(u => {
                if (u.id == action.userId) {
                    u.followed = true;
                }
            })
            return stateCopy;
        }
        case "UNFOLLOW": {
            let stateCopy = {...state};
            stateCopy.users = [...state.users];
            stateCopy.users.map(u => {
                if (u.id == action.userId) {
                    u.followed = false
                }
            })
            return stateCopy;
        }
        case "SET-CURRENT-PAGE": {
            let stateCopy = {...state};
            stateCopy.pageInfo = {...state.pageInfo};
            stateCopy.pageInfo.currentPage = action.currentPage;
            return stateCopy;
        }
        case "SET-USERS": {
            let stateCopy = {...state};
            stateCopy.users = [...action.users]
            return stateCopy;
        }
        case "IS-LOADING": {
            let stateCopy = {...state};
            stateCopy.pageInfo = {...state.pageInfo};
            stateCopy.pageInfo.isLoading = action.isLoading;
            return stateCopy;
        }
        case "IS-FOLLOWING": {
            return {
                ...state,
                pageInfo: {
                    ...state.pageInfo,
                    isFollowing: action.isFollowing
                        ? [...state.pageInfo.isFollowing, action.userId]
                        : state.pageInfo.isFollowing.filter(id => id != action.userId)
                },
            }
        }
        default:
            return state;
    }
}

export const getUsersThunkCreator = (currentPage, pageSize) => {
    console.log('sfasfw')
    return (dispatch) => {
        console.log('2233241')
        dispatch({type: "IS-LOADING", isLoading: true});
        usersAPI.getUsersPage(currentPage, pageSize)
            .then(data => {
                dispatch({type: "SET-USERS", users: data.items});
                dispatch({type: "IS-LOADING", isLoading: false});
            });
    }
}
export default usersReducer;
