let initValue = {
    loginData: {
        id: null,
        email: null,
        login: null,
    }};
const authReducer = (state = initValue, action) => {

    switch (action.type) {
        case "SET-AUTH": {
            let stateCopy = {...state};
            stateCopy.loginData = "15";
            console.log(stateCopy);
            return stateCopy;
        }
        default:
            return state;
    }
}

export default authReducer;
