export let store ={
    state: {
        dialogData: [
            {id: "1", name: "user1"},
            {id: "2", name: "user2"},
            {id: "3", name: "user3"},
            {id: "4", name: "user4"},
            {id: "5", name: "user5"},

        ],
        messageData: [
            {id: "1", text: "Popa"},
            {id: "2", text: "Popa2"},
            {id: "3", text: "Popa3"},
            {id: "4", text: "Popa4"}
        ],
        postData: {
            posts: [
                {id: "1", likesCount: 3, text: "jopa"},
                {id: "2", likesCount: 6, text: "jopaa"},
                {id: "3", likesCount: 2, text: "jopaaa"},
                {id: "4", likesCount: 1, text: "jopaaaaaa"}
            ],
            newPostText: {id: "5", likesCount: 0, text: "asdasda"}
        }
    },

    dispatch(action){
        if(action=="ADD-POST"){
            this._addPost();
        }
        if(action=="CHANGE-POST"){
            this._postTextChange(action);
        }
        else console.log("unexpected action value");
    },
    rerenderEntireTree(){},
    subscriber(observer){
        this.rerenderEntireTree = observer;
    },
    _addPost() {
        this.state.postData.posts.push(Object.assign({}, this.state.postData.newPostText));
        //postTextChange("");
        this.rerenderEntireTree();
    },
    _postTextChange(newText){
        this.state.postData.newPostText.text = newText;
        this.rerenderEntireTree();
    },

    getState(){
        return this.state;
    }

}
window.store = store;
export default store;
