        }
        case "UNFOLLOW": {
            let stateCopy = {...state};
            stateCopy.users = [...state.users];
            stateCopy.users[action.userId].isFollowed = false;
            return stateCopy;
            // action.data.isFollowed=!action.data.isFollowed;
